mapper.select:
SELECT COUNT(*) FROM QUESTION


        AND QUESTION_STATUS = #{filter}



        AND (
            REPLACE(UPPER(QUESTION_TITLE), ' ', '') LIKE '%'||REPLACE(#{searchKeyword}, ' ', '')||'%'
            OR REPLACE(UPPER(QUESTION_CONTENT), ' ', '') LIKE '%'||REPLACE(#{searchKeyword}, ' ', '')||'%'
        )

SELECT * FROM QUESTION 


        AND QUESTION_STATUS = #{filter}


ORDER BY QUESTION_NO ASC
