mapper.select:
SELECT COUNT(*) FROM QUESTION


        AND QUESTION_STATUS = #{filter}

    AND QUESTION_YN = 'Y'

SELECT MAX(QUESTION_NO) 
FROM QUESTION
WHERE QUESTION_NO < #{currentQuestionNo}

SELECT MIN(QUESTION_NO) 
FROM QUESTION
WHERE QUESTION_NO > #{currentQuestionNo}

mapper.update:
UPDATE QUESTION 
SET 
    QUESTION_TITLE = #{questionTitle, jdbcType=VARCHAR},  QUESTION_CONTENT = #{questionContent, jdbcType=VARCHAR} 
WHERE 
    QUESTION_NO = #{questionNo}
